/*******************************
GLOBAL NAVIGATION FOR TALENTUM

2014-12-19 Initial version -Frantic

REQUIREMENTS

- jQuery


USAGE

1)  Add <div id="talentum-nav"></div> inside body element before any other
    content
    
2)  Add id of the active item as data-attibute (data-active)
    e.g. <div id="talentum-nav" data-active="talouselama"></div>
    
3)  Call this script anywhere you want after jQuery (preferably footer)


MODIFYING CONTENT

Add links and their structure to ITEMS variable

An item can have four options:

1)  url (required)

2)  text (required)

3)  mainItem [true/false] (optional, only available for 1st level items)
    
    This means the link will be visible on small screens and not under
    hamburger menu in mobile. Should only be used for the first items in
    the array, otherwise layout breakage will occur.

4)  touchOnly [true/false] (optional, only available for 2nd level items)
    
    This means the link is only shown for touch devices and the parent item
    can be used as a toggle instead of a link.
    => Add as first subItem if other subItems exist, e.g. Talouselämä.

*******************************/

$(function() {
	
	var taleTouchDevice = (('ontouchstart' in window) || (navigator.msMaxTouchPoints > 0)),
		taleNav = $('#talentum-nav'),
		taleActiveItem = taleNav ? taleNav.data('active') : false,
		taleFinalNav,
		/*******************************
		START NAV ITEMS
		*******************************/
		taleItems = {
			talentum: {
				url: 'http://www.talentum.com/fi/',
				text: 'Talentum',
				mainItem: true,
				subItems: {
					talentum_oyj: {
						url: 'http://www.talentum.com/fi/',
						text: 'Talentum Oyj'
					},
					talentum_events: {
						url: 'http://www.talentumevents.fi/',
						text: 'Talentum Events'
					},
					talentum_shop: {
						url: 'http://talentumshop.fi/',
						text: 'Talentum Shop'
					},
					lakimiesliiton_koulutus: {
						url: 'http://www.talentumlakikoulutus.fi/',
						text: 'Lakimiesliiton koulutus'
					},
					talentum_fokus: {
						url: 'http://fokus.talentum.fi/',
						text: 'Talentum Fokus'
					},
					suomenlaki: {
						url: 'http://www.suomenlaki.com/',
						text: 'Suomenlaki.com'
					},
					kaikki_tuotteet_ja_palvelut: {
						url: 'http://www.talentum.fi/',
						text: 'Kaikki tuotteet ja palvelut'
					},
					mediamyynti: {
						url: 'http://mediamyynti.talentum.fi/',
						text: 'Mediamyynti'
					},
					asiakaspalvelu: {
						url: 'http://www.talentum.com/fi/contact/customerservice/',
						text: 'Asiakaspalvelu'
					}
				}
			},
			summa: {
				url: 'http://summa.talentum.fi/',
				text: 'Summa',
				mainItem: true
			},
			talouselama: {
				url: 'http://www.talouselama.fi/',
				text: 'Talouselämä',
				subItems: {
					talouselama: {
						url: 'http://www.talouselama.fi/',
						text: 'Talouselämä',
						touchOnly: true
					},
					fakta: {
						url: 'http://www.talouselama.fi/tyoelama/',
						text: 'Fakta'
					}
				}
			},
			tektal: {
				url: 'http://www.tekniikkatalous.fi/',
				text: 'Tekniikka &amp; Talous',
				subItems: {
					tekniikka_talous: {
						url: 'http://www.tekniikkatalous.fi/',
						text: 'Tekniikka &amp; Talous',
						touchOnly: true
					},
					metallitekniikka: {
						url: 'http://www.tekniikkatalous.fi/metallitekniikka/',
						text: 'Metallitekniikka'
					},
					tekniikan_historia: {
						url: 'http://www.tekniikkatalous.fi/tekniikan_historia/',
						text: 'Tekniikan historia'
					}
				}
			},
			tivi: {
				url: 'http://www.tivi.fi/',
				text: 'Tivi'
			},
			arvopaperi: {
				url: 'http://www.arvopaperi.fi/',
				text: 'Arvopaperi'
			},
			m_m: {
				url: 'http://www.marmai.fi/',
				text: 'M&amp;M'
			},
			mpc: {
				url: 'http://www.mpc.fi/',
				text: 'MPC'
			},
			mediuutiset: {
				url: 'http://www.mediuutiset.fi/',
				text: 'Mediuutiset'
			},
			uratie: {
				url: 'http://www.uratie.fi/',
				text: 'Uratie'
			}
		},
		/*******************************
		END NAV ITEMS
		*******************************/
		css = "<style>@font-face{font-family:'talentumbold';src:url('data:application/x-font-woff;charset=utf-8;base64,') format('woff');font-weight:normal;font-style:normal}#talentum-nav,#talentum-nav nav,#talentum-nav ul,#talentum-nav li,#talentum-nav a{margin:0;padding:0;border:0;color:inherit;font-family:inherit;text-align:inherit;text-decoration:none;font-weight:inherit;line-height:1em}#talentum-nav{position:relative;background:#333;text-align:left;white-space:nowrap;border-top:2px solid #88C657;color:#fff;font-family:'talentumbold', Arial, sans-serif;font-weight:normal;z-index:700;height:34px;margin-bottom:30px}@media (min-width: 920px){#talentum-nav{height:43px}}#talentum-nav,#talentum-nav *,#talentum-nav :before,#talentum-nav :after{-moz-box-sizing:border-box;-webkit-box-sizing:border-box;box-sizing:border-box}#talentum-nav .global-nav-container{max-width:1270px;margin:0 auto;height:inherit !important;overflow:visible !important}@media (min-width: 768px){#talentum-nav .global-nav{display:-webkit-box;display:-webkit-flex;display:-moz-flex;display:-ms-flexbox;display:flex;-webkit-justify-content:space-between;-moz-justify-content:space-between;justify-content:space-between;max-width:1000px;margin-right:48px}}@media (min-width: 920px){#talentum-nav .global-nav{margin-right:130px}}#talentum-nav .global-nav a{display:inherit;height:32px;line-height:32px;padding:0 15px;font-size:14px;text-align:center}@media (min-width: 768px){#talentum-nav .global-nav a{padding:0 8px;font-size:12px}}@media (min-width: 920px){#talentum-nav .global-nav a{height:41px;line-height:41px;padding:0 7px;font-size:14px}}@media (min-width: 1000px){#talentum-nav .global-nav a{padding:0 11px}}#talentum-nav li{display:inline-block}#talentum-nav .global-nav-active>a{color:#88C657}#talentum-nav.global-nav-open .global-nav-secondary{display:block}#talentum-nav .global-nav-parent>a:after{content:'';display:inline-block;width:0;height:0;margin-left:7px;border-style:solid;border-width:4px 3.5px 0;border-color:#fff transparent transparent;vertical-align:middle}@media (max-width: 767px){#talentum-nav .global-nav-secondary{display:none;height:auto;background:#88C657;width:165px}#talentum-nav .global-nav-secondary .global-nav-subnav{position:static;display:block;width:100%}#talentum-nav .global-nav-secondary .global-nav-subnav a{padding-left:30px}#talentum-nav .global-nav-secondary a{border-top:1px solid #94CB68;color:#fff}}@media (max-width: 767px) and (max-width: 767px){#talentum-nav .global-nav-secondary a{text-align:left}#talentum-nav .global-nav-secondary a:after{content:none}}@media (max-width: 767px){.not-touch-device #talentum-nav .global-nav-secondary a:hover{background:#94CB68}}.not-touch-device #talentum-nav li:hover>ul,#talentum-nav .global-nav-open>ul{display:block}.not-touch-device #talentum-nav li:hover>a,#talentum-nav .global-nav-open>a{background:#88C657;color:#fff}#talentum-nav .global-nav-touch{display:none !important}@media (min-width: 768px){.is-touch-device #talentum-nav .global-nav-touch{display:block !important}}#talentum-nav .global-nav-subnav{position:absolute;display:none;background:#88C657;width:165px;white-space:normal}#talentum-nav .global-nav-subnav>li{display:block}#talentum-nav .global-nav-subnav a{display:inherit;height:auto;line-height:normal;padding-top:8px;padding-bottom:7px;border-top:1px solid #94CB68;text-align:left;color:#fff}.not-touch-device #talentum-nav .global-nav-subnav a:hover{background:#94CB68}@media (min-width: 768px){#talentum-nav .global-nav-touch{display:none}}#talentum-nav .global-nav-logo{display:block;float:right;width:21px;height:32px;margin:0 7px 0 20px;overflow:hidden;text-indent:-999px;background:url(data:image/svg+xml;base64,) no-repeat left center}@media (min-width: 920px){#talentum-nav .global-nav-logo{width:112px;height:41px}}#nav-corporate-toggle{display:block;float:left;padding:0;width:44px;height:32px;background:url(data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMjBweCIgaGVpZ2h0PSIxNHB4IiB2aWV3Qm94PSIwIDAgMjAgMTQiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyI+PGcgZmlsbD0iI2ZmZiIgZmlsbC1ydWxlPSJldmVub2RkIj48cGF0aCBkPSJtMCAwaDIwdjJoLTIweiIvPjxwYXRoIGQ9Im0wIDZoMjB2MmgtMjB6Ii8+PHBhdGggZD0ibTAgMTJoMjB2MmgtMjB6Ii8+PC9nPjwvc3ZnPg==) no-repeat center center;cursor:pointer;overflow:hidden;text-indent:-200px}@media (min-width: 768px){#nav-corporate-toggle{display:none;background:none}}.global-nav-open #nav-corporate-toggle{background-color:#88C657}</style>";
		
	if (taleNav) {
		$('head').append(css);
				
		// create nav
		taleFinalNav = '<nav class="global-nav-container" style="height:0;overflow:hidden;">';
		taleFinalNav += '<span id="nav-corporate-toggle">Menu</span>';
		taleFinalNav += '<a class="global-nav-logo" href="http://www.talentum.com">Talentum</a>';

		taleFinalNav += '<ul class="global-nav">';
		
		for (var item in taleItems) {
			taleFinalNav += '<li class="';
			taleFinalNav += (item == taleActiveItem) ? 'global-nav-active' : '';
			taleFinalNav += taleItems[item].subItems ? ' global-nav-parent' : '';
			taleFinalNav += taleItems[item].mainItem ? ' global-nav-primary' : ' global-nav-secondary';
			taleFinalNav += '">';
			taleFinalNav += '<a href="' + taleItems[item].url + '">' + taleItems[item].text + '</a>';
			if (taleItems[item].subItems) {
				taleFinalNav += '<ul class="global-nav-subnav">';
				for (var subItem in taleItems[item].subItems) {
					taleFinalNav += taleItems[item].subItems[subItem].touchOnly ? '<li class="global-nav-touch">' : '<li>';
					taleFinalNav += '<a href="' + taleItems[item].subItems[subItem].url + '">' + taleItems[item].subItems[subItem].text + '</a>';
					taleFinalNav += '</li>';
				}
				taleFinalNav += '</ul>';
			}
			taleFinalNav += '</li>';
		}
		
		taleFinalNav += '</ul></nav>';
		
		taleNav.append(taleFinalNav);
		
		// nav toggle
		$('#nav-corporate-toggle').click(function() {
			if (!$(this).hasClass('global-nav-open')) {
				$('.global-nav-open').removeClass('global-nav-open');
			}
			
			$(this).toggleClass('global-nav-open');
			taleNav.toggleClass('global-nav-open');
		});
		
		// touch device toggle
		if (taleTouchDevice) {
			$('html').addClass('is-touch-device');
			
			$('.global-nav-parent').click(function(e) {
				if (window.innerWidth >= 768 || $(this).hasClass('global-nav-primary')) {
					e.preventDefault();
					if ($(this).hasClass('global-nav-open')) {
						$(this).removeClass('global-nav-open');
					} else {
						$('.global-nav-open').removeClass('global-nav-open');
						$(this).addClass('global-nav-open');
					}
				}
			});
		} else {
			$('html').addClass('not-touch-device');
		}
	}

});
